create function [dbo].[getAttendanceHours](
@ConferenceId bigint,
@CustomerId bigint
)
returns float
as
begin
if (select IsCalc from Conferences where Id=@ConferenceId)=0
	return (select AttendanceHours from Conferences where Id=@ConferenceId)

return isnull((
