	'',(case when @Net>0 then (case @EndType when 0 then '-' when 1 then ' ' when 2 then ' ' when 3 then ' ' else '' end) else '' end),'',(case when @Net>0 then @Net else 0 end),'',@Level-1,0,
	'',(case when @Net<0 then (case @EndType when 0 then '-' when 1 then ' ' when 2 then ' ' when 3 then ' ' else '' end) else '' end),'',(case when @Net<0 then -@Net else 0 end),'',@Level-1,0
end


