	left join SalesMaster M on(T.StoreId=M.StoreId and T.Flag=M.Flag and T.InvoiceNo=M.InvoiceNo)
	left join Items It on(It.Id=T.ItemId)
	where M.DayDate>@DayDate
	and T.Flag in(2)
	and ItemId=@ItemId

	update T 
	set Value=AvgCost,
	Price=AvgCost/Qty
	From SalesDetails T
	left join SalesMaster M on(T.StoreId=M.StoreId and T.Flag=M.Flag and T.InvoiceNo=M.InvoiceNo)
	where M.DayDate>@DayDate
	and T.Flag in(2)
	and Qty<>0
	and ItemId=@ItemId



end
