isnull(MainBal0,0)+((case when '+cast(@MotionType as nvarchar(10))+'=1 then -1. else 1. end)*(isnull(Fn.Db,0)-isnull(Fn.Cr,0)))MainBal0,
--dbo.Bal0(AccNo,Id,'''+cast(@DayDate as varchar(4000))+''',0,0,0) MainBal0,
