CurrencyId,'''' CurrencyName,TelFieldName,
dbo.GetMainAccNoMotionType(AccNo,Id) MotionType,
'+cast(@MotionType as nvarchar(4000))+' MainMotionType,
AccNo,dbo.GetAccName(AccNo)
