CREATE proc [dbo].[GetCaseInvoice]
@CaseInvoiceNo int
--with encryption
as


select *,
dbo.GetCaseArName(T.CaseId)CaseName
from CaseInvoices T
cross join (
			select 8 OrderIndex,'  ' Data,sum(ConsumablesValue)Value
			from OperationMotions 
			where CaseInvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 9 OrderIndex,'  ',sum(Total0)
			from CaseInvoices
			where InvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 10 OrderIndex,' ',sum(Total1)
			from CaseInvoices
			where InvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 6 OrderIndex,dbo.GetServiceGroupName(ServiceGroupId)GroupName,sum(Value)
			from services S
			left join ServiceGroups G on(S.ServiceGroupId=G.Id)
			where S.Flag=2 and S.CaseInvoiceNo=@CaseInvoiceNo
			and G.IsTotal=1
			group by ServiceGroupId
			
			union All
			
			select 7 OrderIndex,dbo.GetServiceTypeName(ServiceGroupId,ServiceTypeId)TypeName,sum(Value)
			from services S
			left join ServiceGroups G on(S.ServiceGroupId=G.Id)
			where S.Flag=2 and S.CaseInvoiceNo=@CaseInvoiceNo
			and G.IsTotal=0
			group by ServiceGroupId,ServiceTypeId
			
			union All
			
			select 1 OrderIndex,dbo.GetOperationMotionsOperationTypesNameAll(InvoiceNo)
			,null
			from OperationMotions 
			where CaseInvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 1 OrderIndex,'  ',Dr1Value+ Dr2Value+ Dr3Value+ AnesthetistValue
			from OperationMotions 
			where CaseInvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 1 OrderIndex,' ',CoValue
			from OperationMotions 
			where CaseInvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 2 OrderIndex,'  ',RoomValue
			from OperationMotions 
			where CaseInvoiceNo=@CaseInvoiceNo
			
			union All
			
			select 3 OrderIndex,'',SUM(Value)
			from(
				select sum(RoomValue2)Value
				from OperationMotions 
				where CaseInvoiceNo=@CaseInvoiceNo
				
				union All
				
				select sum(Living)
				from ClinicsHistory 
				where CaseInvoiceNo=@CaseInvoiceNo
				)TT
			
			union All
			
			select 4 OrderIndex,'',sum(Value)
			from(
				select sum(MedicalValue)Value
				from OperationMotions 
				where CaseInvoiceNo=@CaseInvoiceNo
				
				union all
				
				select sum(Supervision)
				from ClinicsHistory 
				where CaseInvoiceNo=@CaseInvoiceNo
			)TT
			
			union All
			
			select 5 OrderIndex,'  ',sum(Value)
			from(
				select sum(CsValue)Value
				from OperationMotions 
				where CaseInvoiceNo=@CaseInvoiceNo
				
				union All
				
				select sum(Care)
				from ClinicsHistory 
				where CaseInvoiceNo=@CaseInvoiceNo
				)TT
			
)Tbl
where T.InvoiceNo=@CaseInvoiceNo
and Value is not null
order by Tbl.OrderIndex
