			where S.Flag=2 
			--and S.CaseInvoiceNo=@CaseInvoiceNo
			and G.IsTotal=0
			group by S.CaseInvoiceNo,ServiceGroupId,ServiceTypeId
			
			union All
			
			select CaseInvoiceNo,1 OrderIndex,dbo.GetOperationMotionsOperationTypesNameAll(InvoiceNo)
			,null
			from OperationMotions 
