CREATE proc [dbo].[GetInvoicesOne]
@InvoicesFlagId int,
@Year int,
@InvoiceNo int
--with encryption
as

select T.*,
Ty.Name Type_Name,
--Mn.Name MainNo_Name,
--Sn.Name SubNo_Name,
