CREATE proc [dbo].[OpenNewYear]
@NewYear nvarchar(1000)=2015
as


declare @db_name nvarchar(1000)=db_name(),
@Path nvarchar(1000),
@NewPath nvarchar(1000)=2015

declare @New_db_name nvarchar(1000)=substring(@db_name,1,len(@db_name)-4)

